<?php //namespace modules\mod_instantfblogin/** * @package INSTANTFBLOGIN::modules * @subpackage mod_instantfblogin_posts * @author Joomla! Extensions Store * @copyright (C) 2015 - Joomla! Extensions Store * @license GNU/GPLv2 http://www.gnu.org/licenses/gpl-2.0.html */defined('_JEXEC') or die;$widgetHref = $params->get('module_posts_fb_href', null);$widgetWidth = (int)$params->get('module_posts_fb_width', 340);$widgetHeight = (int)$params->get('module_posts_fb_height', 500);$widgetTabs = $params->get('module_posts_fb_tabs', 'timeline');$widgetHideCover = $params->get('module_posts_fb_hide_cover', 0);$widgetShowFacepile = $params->get('module_posts_fb_show_facepile', 1);$widgetSmallHeader = $params->get('module_posts_fb_small_header', 0);$widgetAdaptContainerWidth = $params->get('module_posts_fb_adapt_container_width', 1);// Parse the Facebook page name$explodedHRef = explode('/', $widgetHref);$pageName = array_pop($explodedHRef);?><div class="fb-page" 	data-href="<?php echo $widgetHref;?>" 	data-width="<?php echo $widgetWidth;?>px"	data-height="<?php echo $widgetHeight;?>px"	data-tabs="<?php echo $widgetTabs;?>"	data-hide-cover="<?php echo $widgetHideCover;?>" 	data-show-facepile="<?php echo $widgetShowFacepile;?>"	data-small-header="<?php echo $widgetSmallHeader;?>" 	data-adapt-container-width="<?php echo $widgetAdaptContainerWidth;?>">	<div class="fb-xfbml-parse-ignore">		<blockquote cite="https://www.facebook.com/<?php echo $pageName;?>"><a href="https://www.facebook.com/<?php echo $pageName;?>"><?php echo ucfirst($pageName);?></a></blockquote>	</div></div>